from pynput.keyboard import Controller, Key
import time

keyboard = Controller()

def ecrire(texte):
    """
    Fonction pour simuler la saisie d'un texte, y compris des emojis, avec des flèches pour gérer les lignes.
    :param texte: Texte à écrire.
    """
    print("Préparation à l'écriture...")
    time.sleep(5)  # Pause avant de commencer

    lignes = texte.split("\n")
    nb_lignes = len(lignes)  # Nombre total de lignes dans le texte

    while True:  # Boucle infinie pour réécrire
        for ligne in lignes:
            for char in ligne:
                keyboard.type(char)
                time.sleep(0.05)  # Délai entre chaque caractère

            # Passer à la ligne suivante
            keyboard.press(Key.down)
            keyboard.release(Key.down)

            # Revenir au début de la ligne actuelle
            largeur_ligne = len(ligne.encode('utf-16-le')) // 2
            for _ in range(largeur_ligne):
                keyboard.press(Key.left)
                keyboard.release(Key.left)

        # Après avoir écrit tout le texte, revenir au début du texte
        for _ in range(nb_lignes):  # Remonter du nombre de lignes
            keyboard.press(Key.up)
            keyboard.release(Key.up)
        
        time.sleep(2)  # Attendre 2 secondes avant de recommencer le texte

